mapper.update:
UPDATE TAB_BOARD
SET BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent}, BOARD_UPDATE_DATE = SYSDATE
WHERE BOARD_NUMBER = #{boardNumber}

mapper.select:
SELECT BOARD_NUMBER, BOARD_TITLE, BOARD_CONTENT, BOARD_REGISTER_DATE, BOARD_UPDATE_DATE, USER_NUMBER, USER_ID, FILE_UPLOAD_PATH, FILE_UUID, FILE_NAME FROM( SELECT ROWNUM AS RNUM, BOARD_NUMBER, BOARD_TITLE, BOARD_CONTENT, BOARD_REGISTER_DATE, BOARD_UPDATE_DATE, USER_NUMBER, USER_ID, FILE_UPLOAD_PATH, FILE_UUID, FILE_NAME FROM( SELECT B.BOARD_NUMBER, BOARD_TITLE, BOARD_CONTENT, BOARD_REGISTER_DATE, BOARD_UPDATE_DATE, B.USER_NUMBER, USER_ID, J.FILE_UPLOAD_PATH, J.FILE_UUID, FILE_NAME FROM TAB_USER U JOIN TAB_BOARD B ON U.USER_NUMBER = B.USER_NUMBER LEFT JOIN ( SELECT FILE_NUMBER, FILE_UPLOAD_PATH, FILE_UUID, FILE_NAME, BOARD_NUMBER FROM ( SELECT FILE_NUMBER, FILE_UPLOAD_PATH, FILE_UUID, FILE_NAME, BOARD_NUMBER, RANK() OVER(PARTITION BY BOARD_NUMBER ORDER BY FILE_NUMBER) RK FROM TAB_FILE F ) WHERE RK = 1 ) J ON B.BOARD_NUMBER = J.BOARD_NUMBER ORDER BY B.BOARD_NUMBER DESC ) S1
WHERE ROWNUM <= #{page} * #{amount}
) S2 WHERE RNUM > (#{page} - 1) * #{amount}
